const todos = (state = [], action) => {
  switch (action.type) {
    case "ADD_TODO":
      return [
        ...state,
        {
          id: action.id,
          text: action.text,
          completed: false,
        },
      ];
    case "TOGGLE_TODO":
      return state.map((td) =>
        td.id === action.id ? { ...td, completed: !todos.completed } : td
      );
    default:
      return state;
  }
};

export default todos;
