import React, { useEffect, useContext } from "react";
import { Data, Dispatch } from "./Context";

function Question() {
  const dispatch = useContext(Dispatch);
  const globalData = useContext(Data);

  useEffect(() => {
    const fetchQuestions = async () => {
      const response = await fetch("/api");
      const resData = await response.json();
      const questionsData = resData.questionsFind;
      const randomQuestion = Math.floor(Math.random() * questionsData);
      console.log(resData.questionsFind[randomQuestion].question, "<<<");
      console.log(resData.questionsFind[randomQuestion].answer, "<<<");

      // console.log(shitFind[randomQuestion].question);
      // console.log(shitFind[randomQuestion].answer);
      // dispatch({
      //   type: "QUESTION_ANSWER_INSERT",
      //   question: resData.questions[randomQuestion].question,
      //   answer: shitFind[randomQuestion].answer,
      // });
    };
    fetchQuestions();
  }, []);

  return (
    <div className="App__container--question">
      <span className="App__container--question--bf">-</span>
      {/* {console.log(globalData)} */}
    </div>
  );
}

export default Question;
