import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchData } from "../asyncActions/fetchQuestions";
import { selectQuestions } from "../slices/rootSlice";

function Question() {
  const dataIsFetched = useSelector((d) => d.rootReducer.dataIsFetched);
  const questions = useSelector(selectQuestions);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchData());
  }, []);

  return (
    <div className="App__container--question">
      <span className="App__container--question--bf">-</span>
    </div>
  );
}

export default Question;
