import { useSelector } from "react-redux";
import { fetchData } from "../asyncActions/fetchQuestions";

const { createSlice } = require("@reduxjs/toolkit");

export const rootSlice = createSlice({
  name: "rootReducer",
  initialState: {
    data: [],
    question: "",
    answer: "",
    possibleAnswers: "",
    dataIsFetched: false,
  },
  reducers: {},
  extraReducers: {
    [fetchData.fulfilled]: (state, action) => {
      state.data = action.payload;
      const questionsFind = action.payload.questionsFind;
      const maxLength = questionsFind.length;
      let random = Math.floor(Math.random() * maxLength);
      console.log(random);
      state.question = questionsFind[random];
      // console.log(action.payload.questionsFind, " <");

      // console.log("fullfiled happen");
      state.dataIsFetched = true;
    },
  },
});

export const {} = rootSlice.actions;

export const selectQuestions = (state) => state.rootReducer.data;
export default rootSlice.reducer;
