import React, { useState, useEffect } from "react";

function Answers() {
  const [answerGotClicked, setAnswerGotClicked] = useState(false);
  const [questionData, setQuestionData] = useState([]);

  useEffect(() => {
    setQuestionData([
      { question: "Lorem ipsum dolor sit" },
      { question1: "Lorem ipsum dolor sit" },
      { question2: "Lorem ipsum dolor sit" },
      { question3: "Lorem ipsum dolor sit" },
    ]);
  }, []);

  function answerClicked() {
    setAnswerGotClicked(true);
    console.log(answerGotClicked);
    console.log(questionData);
  }

  return (
    <ul className="App__container--list">
      <li onClick={answerClicked} className="App__container--list--answer">
        <span>Lorem ipsum dolor sit</span>
      </li>
      <li className="App__container--list--answer">
        <span>Lorem ipsum dolor sit</span>
      </li>
      <li className="App__container--list--answer">
        <span>Lorem ipsum dolor sit</span>
      </li>
      <li className="App__container--list--answer">
        <span>Lorem ipsum dolor sit</span>
      </li>
    </ul>
  );
}

export default Answers;
