import { VisibiltyFilters } from "../actions";

const visibilityFilter = (state = VisibiltyFilters.SHOW_ALL, action) => {
  switch (action.type) {
    case "SET_VISIBILTY_FILTER":
      return action.filter;
    default:
      return state;
  }
};

export default visibilityFilter;
