import React, { useState, useEffect, useContext } from "react";
import { Data, Dispatch } from "./Context";

function Answers() {
  const [answerGotClicked, setAnswerGotClicked] = useState(false);
  const [questionData, setQuestionData] = useState([]);
  const dispatch = useContext(Dispatch);
  const data = useContext(Data);

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch("localhost:5000/questions");
      const data = await response.json();
      console.log(data);
    };
    fetchData();
  }, []);

  function answerClicked() {
    setAnswerGotClicked(true);
    console.log(answerGotClicked);
    console.log(questionData);
    dispatch({ type: "muda" });
  }

  return (
    <ul className="App__container--list">
      {questionData.map((data) => (
        <li
          key={data.key}
          onClick={answerClicked}
          className="App__container--list--answer"
        >
          <span>{data.person}</span>
        </li>
      ))}
    </ul>
  );
}

export default Answers;
